                updateContextPanel();

                // In the event a gradient was flipped:
                if(selectedElement && mode === "select") {
                    Editor.paintBox.fill.update();
                    Editor.paintBox.stroke.update();
                }

                svgCanvas.runExtensions("elementChanged", {
                    elems: elems
                });
