        function loadSvgString(str, callback) {
            var success = svgCanvas.setSvgString(str) !== false;
            callback = callback || $.noop;
            if(success) {
                methodDraw.processFile();
                callback(true);
            } else {
                $.alert(uiStrings.notification.errorLoadingSVG, function() {
                    callback(false);
                });
            }
        }

